package main;import obj.Card;import obj.GameManager;import obj.Player;import java.util.ArrayList;import java.util.Scanner;public class Main {    public static void main(String[] args) {        Scanner sc = new Scanner(System.in);        Card card = new Card();        ArrayList<Integer> cards = card.setDeck();        GameManager gm = new GameManager();        Player A = new Player();        Player B = new Player();        boolean flag = true;        while (flag) {            gm.menu();            int choose = sc.nextInt();            if (choose == 1) {                gm.gameStart();                System.out.println("CNT : " + card.getCnt());                A.getMyDeck().add(card.rollDeck());                B.getMyDeck().add(card.rollDeck());                System.out.println("A : " );                for (int i = 0; i < A.getMyDeck().size(); i++) {                    System.out.print(A.getMyDeck().get(i) + ", ");                }                System.out.println("");                System.out.println("B : " );                for (int i = 0; i < B.getMyDeck().size(); i++) {                    System.out.print(B.getMyDeck().get(i) + ", ");                }                System.out.println("");            } else {                gm.gameEnd();            }        }    }}